mapper.select:
	select share_idx,sharefield,sharename,simpleinfo,sharecontent,sharemoney,sharedateen,sharedate,rename_file
                from(
                    select rownum rnum , s3.*
			from(
				select s.sharefield,s.sharename,s.simpleinfo,s.sharecontent,s.sharemoney,s.sharedateen,s.share_idx,s.sharedate,st.rename_file 
				from shar s, share_thumb st
                where s.share_idx = st.share_idx


			      and s.sharefield = #{filter}


			      and 1=1



				order by s.share_idx desc
			)s3
		)where rnum between #{start} and #{end}
