mapper.select:
select nvl(payper,0) payper,nvl(pay,0)
pay,share_idx,sharefield,sharename,simpleinfo,sharecontent,sharemoney,sharedateen,ROUND(sharedate-sysdate)
day,sharedate,rename_file,member_id
from(
select rownum rnum , s3.*
from(
select ROUND((sum(sp.payment)/s.sharemoney) * 100,1)
payper,s.member_id,sum(sp.payment)
pay,s.sharefield,s.sharename,s.simpleinfo,s.sharecontent,s.sharemoney,s.sharedateen,s.share_idx,s.sharedate,st.rename_file
from shar s, share_thumb st,share_pay sp
where s.share_idx = st.share_idx
and s.share_idx = sp.share_idx(+)


		and s.sharefield = #{filter}


		and 1=1


group by s.sharefield, s.sharename, s.simpleinfo, s.sharecontent,
s.sharemoney,
s.sharedateen, s.share_idx, s.sharedate, st.rename_file,s.member_id
order by s.share_idx desc
)s3
)where rnum between #{start} and #{end}

select nvl(sum(sharemoney*like_check),0)
from SHARE_Like c,shar p

where c.share_idx=p.share_idx and member_id=#{member_id}

mapper.update:
UPDATE SHARE_Like
SET like_check = #{like_check}
WHERE member_id=#{member_id} and
share_idx = #{share_idx}

mapper.select:
SELECT COUNT(*)
FROM SHARE_Like
WHERE member_id= #{member_id}
AND share_idx = #{share_idx}

mapper.update:
UPDATE SHARE_Like
SET like_check = #{like_check}
WHERE member_id=#{member_id} and
share_idx = #{share_idx}

